package com.deitel.deitalpractice;

public class Exercise_4_16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = -2;
		int total = 0;
		while(x<=10) {
			int y = x +2;
			x++;
			total += y;
			System.out.printf("Y is %d and total is %d", y, total);
			System.out.println();
		}
		

	}

}
